        /// </summary>
        [EnumMember(Value = "REAL_CURRENCY")]
        RealCurrency,

        /// <summary>
        /// values representing truth of logic or Boolean algebra (TRUE, FALSE)
        /// </summary>
        [EnumMember(Value = "BOOLEAN")]
