        {
            return transformer.Transform(this, context);
        }

        public EventPayload(
            Reference source,
            Reference observableReference,
            string timeStamp,
            Reference? sourceSemanticId = null,
            Reference? observableSemanticId = null,
            string? topic = null,
            Reference? subjectId = null,
            string? payload = null)
        {
            Source = source;
