        /// <summary>
        /// Iterate over all the class instances referenced from this instance
        /// without further recursion.
        /// </summary>
        public IEnumerable<IClass> DescendOnce()
        {
            if (Extensions != null)
            {
                foreach (var anItem in Extensions)
                {
                    yield return anItem;
                }
            }

            if (DisplayName != null)
            {
                yield return DisplayName;
            }

            if (Description != null)
