                    yield return anItem;
                }
            }

            if (DataSpecifications != null)
            {
                foreach (var anItem in DataSpecifications)
                {
                    yield return anItem;
                }
            }

            yield return Observed;

            if (MessageBroker != null)
            {
                yield return MessageBroker;
            }
        }

