                foreach (var anItem in DataSpecifications)
                {
                    yield return anItem;

                    // Recurse
                    foreach (var anotherItem in anItem.Descend())
                    {
                        yield return anotherItem;
                    }
                }
            }

            if (IsCaseOf != null)
            {
                foreach (var anItem in IsCaseOf)
